mapper.select:
select sido_code as sidoCode, gugun_code as gugunCode, gugun_name as gugunName
from trip.guguns
where sido_code = #{sidoCode}

select spot_id as spotId ,title as title ,content_type_id as contentTypeId ,area_code as sidoCode ,si_gun_gu_code as sigunguCode ,first_image1 as imagePath1 ,first_image2 as imagePath2 ,map_level as mapLevel ,latitude as latitude ,longitude as longitude ,addr1 as frontAddress ,addr2 as rearAddress from trip.spot where 1=1
and area_code = #{areaCode}
and si_gun_gu_code = #{siGunGuCode}
and content_type_id = #{contentTypeId}
AND MATCH(title, addr1) AGAINST(#{keyword} IN NATURAL LANGUAGE MODE)

SELECT spot_id AS spotId, title, content_type_id AS contentTypeId, area_code AS sidoCode, si_gun_gu_code AS sigunguCode, first_image1 AS imagePath1, first_image2 AS imagePath2, map_level AS mapLevel, latitude, longitude, addr1 AS frontAddress, addr2 AS rearAddress
FROM spot
WHERE ST_Contains( ST_MakeEnvelope( ST_GeomFromText(CONCAT('POINT(', #{minLng}, ' ', #{minLat}, ')')), ST_GeomFromText(CONCAT('POINT(', #{maxLng}, ' ', #{maxLat}, ')')) ), location )
AND spot_id > #{cursor}
ORDER BY spot_id ASC LIMIT #{limit}
