mapper.select:
SELECT user_id, user_name, user_password, email_id, created_at, updated_at, role_id
FROM trip.users
WHERE user_id = #{user_id}

mapper.update:
UPDATE trip.users
SET user_name = #{user_name},user_password = #{user_password},email_id = #{email_id}, updated_at = NOW()
WHERE user_id = #{user_id}
